import React, { useState, useCallback } from 'react';
import { DragDropContext, DropResult } from 'react-beautiful-dnd';
import styled from 'styled-components';
import ElementPanel from './components/ElementPanel';
import BuilderCanvas from './components/BuilderCanvas';
import PropertyPanel from './components/PropertyPanel';

const AppContainer = styled.div`
  display: flex;
  height: 100vh;
  overflow: hidden;
`;

const App: React.FC = () => {
  const [elements, setElements] = useState<any[]>([]);
  const [selectedElement, setSelectedElement] = useState<string | null>(null);

  const getDefaultProperties = useCallback((type: string) => {
    switch (type) {
      case 'text':
        return { content: 'New text block' };
      case 'heading':
        return { content: 'New Heading', level: 'h2' };
      case 'image':
        return { src: 'https://via.placeholder.com/300x200', alt: 'Placeholder image' };
      case 'button':
        return { text: 'Click me', backgroundColor: '#2196f3', color: 'white' };
      case 'divider':
        return { style: 'solid', color: '#dee2e6' };
      case 'spacer':
        return { height: '20px' };
      case 'video':
        return { url: 'https://www.youtube.com/embed/dQw4w9WgXcQ' };
      case 'form':
        return { fields: [{ type: 'text', label: 'Name' }] };
      case 'social':
        return { links: [{ platform: 'twitter', url: '#' }] };
      case 'map':
        return { location: 'New York, NY', zoom: 12 };
      default:
        return {};
    }
  }, []);

  const handleDragEnd = useCallback((result: DropResult) => {
    const { source, destination, draggableId } = result;

    // If dropped outside a droppable area
    if (!destination) return;

    // If dropped in the same position
    if (
      source.droppableId === destination.droppableId &&
      source.index === destination.index
    ) {
      return;
    }

    // Handle initial drop from panel to canvas
    if (source.droppableId === 'panel' && destination.droppableId === 'canvas') {
      const elementType = draggableId.replace('panel-', '');
      const newElement = {
        id: `${elementType}-${Date.now()}`,
        type: elementType,
        ...getDefaultProperties(elementType),
      };
      
      setElements(prevElements => {
        const newElements = Array.from(prevElements);
        newElements.splice(destination.index, 0, newElement);
        return newElements;
      });
      setSelectedElement(newElement.id);
      return;
    }

    // Handle reordering within canvas
    if (source.droppableId === 'canvas' && destination.droppableId === 'canvas') {
      setElements(prevElements => {
        const newElements = Array.from(prevElements);
        const [removed] = newElements.splice(source.index, 1);
        newElements.splice(destination.index, 0, removed);
        return newElements;
      });
    }
  }, [getDefaultProperties]);

  const handleElementUpdate = useCallback((id: string, updates: any) => {
    setElements(prevElements => 
      prevElements.map(element => 
        element.id === id ? { ...element, ...updates } : element
      )
    );
  }, []);

  return (
    <DragDropContext onDragEnd={handleDragEnd}>
      <AppContainer>
        <ElementPanel />
        <BuilderCanvas
          elements={elements}
          setElements={setElements}
          selectedElement={selectedElement}
          setSelectedElement={setSelectedElement}
        />
        <PropertyPanel
          selectedElement={elements.find(e => e.id === selectedElement)}
          onUpdate={handleElementUpdate}
        />
      </AppContainer>
    </DragDropContext>
  );
};

export default App;
