import React from 'react';
import { DragDropContext, Droppable } from 'react-beautiful-dnd';
import styled from 'styled-components';
import BuilderCanvas from './components/BuilderCanvas';
import ElementPanel from './components/ElementPanel';
import PropertyPanel from './components/PropertyPanel';

const AppContainer = styled.div`
  display: flex;
  height: 100vh;
  background-color: #f5f5f5;
`;

const MainContent = styled.div`
  flex: 1;
  display: flex;
  flex-direction: column;
  padding: 20px;
  gap: 20px;
`;

const App: React.FC = () => {
  const [selectedElement, setSelectedElement] = React.useState<string | null>(null);
  const [elements, setElements] = React.useState<any[]>([]);

  const onDragEnd = (result: any) => {
    if (!result.destination) return;

    const { source, destination } = result;

    // Handle reordering of elements
    if (source.droppableId === destination.droppableId) {
      const newElements = Array.from(elements);
      const [removed] = newElements.splice(source.index, 1);
      newElements.splice(destination.index, 0, removed);
      setElements(newElements);
    }
  };

  return (
    <AppContainer>
      <ElementPanel />
      <DragDropContext onDragEnd={onDragEnd}>
        <MainContent>
          <BuilderCanvas
            elements={elements}
            setElements={setElements}
            selectedElement={selectedElement}
            setSelectedElement={setSelectedElement}
          />
        </MainContent>
      </DragDropContext>
      <PropertyPanel selectedElement={selectedElement} elements={elements} setElements={setElements} />
    </AppContainer>
  );
};

export default App;
