import React from 'react';
import styled from 'styled-components';

const Panel = styled.div`
  width: 300px;
  background-color: #f8f9fa;
  border-left: 1px solid #dee2e6;
  padding: 20px;
  overflow-y: auto;
`;

const Section = styled.div`
  margin-bottom: 20px;
`;

const Label = styled.label`
  display: block;
  margin-bottom: 5px;
  color: #495057;
  font-weight: 500;
`;

const Input = styled.input`
  width: 100%;
  padding: 8px;
  border: 1px solid #ced4da;
  border-radius: 4px;
  margin-bottom: 10px;

  &:focus {
    outline: none;
    border-color: #2196f3;
  }
`;

const Select = styled.select`
  width: 100%;
  padding: 8px;
  border: 1px solid #ced4da;
  border-radius: 4px;
  margin-bottom: 10px;

  &:focus {
    outline: none;
    border-color: #2196f3;
  }
`;

const ColorInput = styled.input`
  width: 100%;
  height: 40px;
  padding: 4px;
  border: 1px solid #ced4da;
  border-radius: 4px;
  margin-bottom: 10px;
`;

const Button = styled.button`
  padding: 8px 16px;
  background-color: #2196f3;
  color: white;
  border: none;
  border-radius: 4px;
  cursor: pointer;
  margin-top: 10px;

  &:hover {
    background-color: #1976d2;
  }
`;

interface PropertyPanelProps {
  selectedElement: any;
  onUpdate: (id: string, updates: any) => void;
}

const PropertyPanel: React.FC<PropertyPanelProps> = ({ selectedElement, onUpdate }) => {
  if (!selectedElement) {
    return (
      <Panel>
        <p>Select an element to edit its properties</p>
      </Panel>
    );
  }

  const handleChange = (field: string, value: any) => {
    onUpdate(selectedElement.id, { [field]: value });
  };

  const renderProperties = () => {
    switch (selectedElement.type) {
      case 'text':
        return (
          <Section>
            <Label>Content</Label>
            <Input
              type="text"
              value={selectedElement.content}
              onChange={(e) => handleChange('content', e.target.value)}
            />
          </Section>
        );
      case 'heading':
        return (
          <>
            <Section>
              <Label>Content</Label>
              <Input
                type="text"
                value={selectedElement.content}
                onChange={(e) => handleChange('content', e.target.value)}
              />
            </Section>
            <Section>
              <Label>Level</Label>
              <Select
                value={selectedElement.level}
                onChange={(e) => handleChange('level', e.target.value)}
              >
                <option value="h1">H1</option>
                <option value="h2">H2</option>
                <option value="h3">H3</option>
                <option value="h4">H4</option>
              </Select>
            </Section>
          </>
        );
      case 'image':
        return (
          <>
            <Section>
              <Label>Image URL</Label>
              <Input
                type="text"
                value={selectedElement.src}
                onChange={(e) => handleChange('src', e.target.value)}
              />
            </Section>
            <Section>
              <Label>Alt Text</Label>
              <Input
                type="text"
                value={selectedElement.alt}
                onChange={(e) => handleChange('alt', e.target.value)}
              />
            </Section>
          </>
        );
      case 'button':
        return (
          <>
            <Section>
              <Label>Text</Label>
              <Input
                type="text"
                value={selectedElement.text}
                onChange={(e) => handleChange('text', e.target.value)}
              />
            </Section>
            <Section>
              <Label>Background Color</Label>
              <ColorInput
                type="color"
                value={selectedElement.backgroundColor}
                onChange={(e) => handleChange('backgroundColor', e.target.value)}
              />
            </Section>
            <Section>
              <Label>Text Color</Label>
              <ColorInput
                type="color"
                value={selectedElement.color}
                onChange={(e) => handleChange('color', e.target.value)}
              />
            </Section>
          </>
        );
      case 'divider':
        return (
          <>
            <Section>
              <Label>Style</Label>
              <Select
                value={selectedElement.style}
                onChange={(e) => handleChange('style', e.target.value)}
              >
                <option value="solid">Solid</option>
                <option value="dashed">Dashed</option>
                <option value="dotted">Dotted</option>
              </Select>
            </Section>
            <Section>
              <Label>Color</Label>
              <ColorInput
                type="color"
                value={selectedElement.color}
                onChange={(e) => handleChange('color', e.target.value)}
              />
            </Section>
          </>
        );
      case 'spacer':
        return (
          <Section>
            <Label>Height (px)</Label>
            <Input
              type="number"
              value={parseInt(selectedElement.height)}
              onChange={(e) => handleChange('height', `${e.target.value}px`)}
            />
          </Section>
        );
      case 'video':
        return (
          <Section>
            <Label>Video URL</Label>
            <Input
              type="text"
              value={selectedElement.url}
              onChange={(e) => handleChange('url', e.target.value)}
            />
          </Section>
        );
      case 'form':
        return (
          <Section>
            <Label>Form Fields</Label>
            {selectedElement.fields.map((field: any, index: number) => (
              <div key={index}>
                <Input
                  type="text"
                  value={field.label}
                  onChange={(e) => {
                    const newFields = [...selectedElement.fields];
                    newFields[index] = { ...field, label: e.target.value };
                    handleChange('fields', newFields);
                  }}
                />
              </div>
            ))}
            <Button
              onClick={() => {
                const newFields = [...selectedElement.fields, { type: 'text', label: 'New Field' }];
                handleChange('fields', newFields);
              }}
            >
              Add Field
            </Button>
          </Section>
        );
      case 'social':
        return (
          <Section>
            <Label>Social Links</Label>
            {selectedElement.links.map((link: any, index: number) => (
              <div key={index}>
                <Input
                  type="text"
                  value={link.platform}
                  onChange={(e) => {
                    const newLinks = [...selectedElement.links];
                    newLinks[index] = { ...link, platform: e.target.value };
                    handleChange('links', newLinks);
                  }}
                />
                <Input
                  type="text"
                  value={link.url}
                  onChange={(e) => {
                    const newLinks = [...selectedElement.links];
                    newLinks[index] = { ...link, url: e.target.value };
                    handleChange('links', newLinks);
                  }}
                />
              </div>
            ))}
            <Button
              onClick={() => {
                const newLinks = [...selectedElement.links, { platform: 'New Platform', url: '#' }];
                handleChange('links', newLinks);
              }}
            >
              Add Link
            </Button>
          </Section>
        );
      case 'map':
        return (
          <>
            <Section>
              <Label>Location</Label>
              <Input
                type="text"
                value={selectedElement.location}
                onChange={(e) => handleChange('location', e.target.value)}
              />
            </Section>
            <Section>
              <Label>Zoom Level</Label>
              <Input
                type="number"
                min="1"
                max="20"
                value={selectedElement.zoom}
                onChange={(e) => handleChange('zoom', parseInt(e.target.value))}
              />
            </Section>
          </>
        );
      default:
        return null;
    }
  };

  return (
    <Panel>
      <h3>Properties</h3>
      {renderProperties()}
    </Panel>
  );
};

export default PropertyPanel;
