import React from 'react';
import { Draggable } from 'react-beautiful-dnd';
import styled from 'styled-components';

const PanelContainer = styled.div`
  width: 250px;
  background-color: white;
  border-right: 1px solid #e0e0e0;
  padding: 20px;
  overflow-y: auto;
`;

const ElementList = styled.div`
  display: flex;
  flex-direction: column;
  gap: 12px;
`;

const ElementItem = styled.div`
  padding: 12px;
  background-color: #f5f5f5;
  border-radius: 4px;
  cursor: grab;
  display: flex;
  align-items: center;
  gap: 8px;
  transition: background-color 0.2s;

  &:hover {
    background-color: #e0e0e0;
  }

  &:active {
    cursor: grabbing;
  }
`;

const ElementIcon = styled.div`
  width: 24px;
  height: 24px;
  display: flex;
  align-items: center;
  justify-content: center;
  background-color: #2196f3;
  color: white;
  border-radius: 4px;
`;

const elements = [
  {
    id: 'text-element',
    type: 'text',
    label: 'Text Block',
    icon: 'T',
    defaultContent: 'Add your text here',
  },
  {
    id: 'image-element',
    type: 'image',
    label: 'Image',
    icon: 'ðŸ–¼ï¸',
    defaultSrc: 'https://via.placeholder.com/150',
    defaultAlt: 'Placeholder image',
  },
  {
    id: 'button-element',
    type: 'button',
    label: 'Button',
    icon: 'ðŸ”˜',
    defaultText: 'Click me',
    defaultBackgroundColor: '#2196f3',
    defaultColor: 'white',
  },
];

const ElementPanel: React.FC = () => {
  return (
    <PanelContainer>
      <h2>Elements</h2>
      <ElementList>
        {elements.map((element, index) => (
          <Draggable
            key={element.id}
            draggableId={`panel-${element.id}`}
            index={index}
          >
            {(provided) => (
              <ElementItem
                ref={provided.innerRef}
                {...provided.draggableProps}
                {...provided.dragHandleProps}
              >
                <ElementIcon>{element.icon}</ElementIcon>
                <span>{element.label}</span>
              </ElementItem>
            )}
          </Draggable>
        ))}
      </ElementList>
    </PanelContainer>
  );
};

export default ElementPanel;
