import React from 'react';
import { DragDropContext, Droppable } from 'react-beautiful-dnd';
import styled from 'styled-components';
import BuilderCanvas from './components/BuilderCanvas';
import ElementPanel from './components/ElementPanel';
import PropertyPanel from './components/PropertyPanel';

const AppContainer = styled.div`
  display: flex;
  height: 100vh;
  background-color: #f5f5f5;
`;

const MainContent = styled.div`
  flex: 1;
  display: flex;
  flex-direction: column;
  padding: 20px;
  gap: 20px;
`;

const App: React.FC = () => {
  const [selectedElement, setSelectedElement] = React.useState<string | null>(null);
  const [elements, setElements] = React.useState<any[]>([]);

  const onDragEnd = (result: any) => {
    if (!result.destination) return;

    const { source, destination, draggableId } = result;

    // Handle initial drop from panel to canvas
    if (source.droppableId === 'panel' && destination.droppableId === 'canvas') {
      const elementType = draggableId.replace('panel-', '');
      const newElement = {
        id: `${elementType}-${Date.now()}`,
        type: elementType,
        ...(elementType === 'text' && { content: 'Add your text here' }),
        ...(elementType === 'image' && {
          src: 'https://via.placeholder.com/150',
          alt: 'Placeholder image',
        }),
        ...(elementType === 'button' && {
          text: 'Click me',
          backgroundColor: '#2196f3',
          color: 'white',
        }),
      };
      setElements([...elements, newElement]);
      return;
    }

    // Handle reordering of elements
    if (source.droppableId === destination.droppableId) {
      const newElements = Array.from(elements);
      const [removed] = newElements.splice(source.index, 1);
      newElements.splice(destination.index, 0, removed);
      setElements(newElements);
    }
  };

  return (
    <DragDropContext onDragEnd={onDragEnd}>
      <AppContainer>
        <ElementPanel />
        <MainContent>
          <BuilderCanvas
            elements={elements}
            setElements={setElements}
            selectedElement={selectedElement}
            setSelectedElement={setSelectedElement}
          />
        </MainContent>
        <PropertyPanel selectedElement={selectedElement} elements={elements} setElements={setElements} />
      </AppContainer>
    </DragDropContext>
  );
};

export default App;
