import React, { useState, useCallback } from 'react';
import { DragDropContext, DropResult } from 'react-beautiful-dnd';
import styled from 'styled-components';
import ElementPanel from './components/ElementPanel';
import BuilderCanvas from './components/BuilderCanvas';
import PropertyPanel from './components/PropertyPanel';

const AppContainer = styled.div`
  display: flex;
  height: 100vh;
  overflow: hidden;
`;

const getDefaultProperties = (type: string) => {
  switch (type) {
    case 'text':
      return { content: 'New text block' };
    case 'heading':
      return { content: 'New heading' };
    case 'image':
      return { src: 'https://via.placeholder.com/300x200', alt: 'Placeholder image' };
    case 'button':
      return { text: 'Click me', backgroundColor: '#007bff', color: 'white' };
    case 'divider':
      return { color: '#dee2e6' };
    case 'spacer':
      return { height: '20px' };
    case 'video':
      return { url: 'https://www.youtube.com/embed/dQw4w9WgXcQ' };
    case 'form':
      return {
        fields: [
          { type: 'text', label: 'Name' },
          { type: 'email', label: 'Email' },
          { type: 'textarea', label: 'Message' }
        ]
      };
    case 'social':
      return {
        links: [
          { platform: 'Facebook', url: '#' },
          { platform: 'Twitter', url: '#' },
          { platform: 'Instagram', url: '#' }
        ]
      };
    case 'map':
      return { location: 'New York, NY', zoom: 12 };
    default:
      return {};
  }
};

const App: React.FC = () => {
  const [elements, setElements] = useState<any[]>([]);
  const [selectedElement, setSelectedElement] = useState<string | null>(null);

  const handleDragEnd = useCallback((result: DropResult) => {
    const { source, destination, draggableId } = result;

    // If dropped outside a droppable area
    if (!destination) return;

    // If dropped in the canvas from the panel
    if (source.droppableId === 'panel' && destination.droppableId === 'canvas') {
      const elementType = draggableId.replace('panel-', '');
      const newElement = {
        id: `${elementType}-${Date.now()}`,
        type: elementType,
        ...getDefaultProperties(elementType)
      };

      setElements(prev => [...prev, newElement]);
      return;
    }

    // If reordering within the canvas
    if (source.droppableId === 'canvas' && destination.droppableId === 'canvas') {
      const newElements = Array.from(elements);
      const [removed] = newElements.splice(source.index, 1);
      newElements.splice(destination.index, 0, removed);
      setElements(newElements);
    }
  }, [elements]);

  const handleElementUpdate = useCallback((id: string, properties: any) => {
    setElements(prev => prev.map(element => 
      element.id === id ? { ...element, ...properties } : element
    ));
  }, []);

  return (
    <DragDropContext onDragEnd={handleDragEnd}>
      <AppContainer>
        <ElementPanel />
        <BuilderCanvas
          elements={elements}
          setElements={setElements}
          selectedElement={selectedElement}
          setSelectedElement={setSelectedElement}
        />
        {selectedElement && (
          <PropertyPanel
            element={elements.find(e => e.id === selectedElement)}
            onUpdate={(properties) => handleElementUpdate(selectedElement, properties)}
          />
        )}
      </AppContainer>
    </DragDropContext>
  );
};

export default App;
