import React from 'react';
import styled from 'styled-components';
import { useDroppable } from '@dnd-kit/core';
import { useSortable } from '@dnd-kit/sortable';
import { CSS } from '@dnd-kit/utilities';

const CanvasContainer = styled.div`
  flex: 1;
  padding: 20px;
  background-color: #f8f9fa;
  min-height: 100vh;
  overflow-y: auto;
`;

const Canvas = styled.div<{ isOver?: boolean }>`
  background-color: ${props => props.isOver ? '#f0f0f0' : 'white'};
  border: 1px solid #dee2e6;
  border-radius: 4px;
  min-height: 100%;
  padding: 20px;
  max-width: 1200px;
  margin: 0 auto;
  transition: background-color 0.2s;
`;

const ElementWrapper = styled.div<{ isSelected: boolean }>`
  padding: 10px;
  margin: 5px 0;
  border: 2px solid ${props => props.isSelected ? '#2196f3' : 'transparent'};
  border-radius: 4px;
  cursor: pointer;
  transition: all 0.2s;
  background-color: white;

  &:hover {
    background-color: #f8f9fa;
  }
`;

const Heading = styled.h2`
  margin: 0;
  font-size: 24px;
  color: #333;
`;

const Divider = styled.hr<{ color: string }>`
  border: none;
  border-top: 1px solid ${props => props.color};
  margin: 10px 0;
`;

const Spacer = styled.div<{ height: string }>`
  height: ${props => props.height};
`;

const VideoContainer = styled.div`
  position: relative;
  padding-bottom: 56.25%;
  height: 0;
  overflow: hidden;

  iframe {
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    border: none;
  }
`;

const Form = styled.form`
  display: flex;
  flex-direction: column;
  gap: 10px;
`;

const FormField = styled.div`
  display: flex;
  flex-direction: column;
  gap: 5px;
`;

const SocialLinks = styled.div`
  display: flex;
  gap: 10px;
  flex-wrap: wrap;
`;

const MapContainer = styled.div`
  width: 100%;
  height: 300px;
  background-color: #f8f9fa;
  display: flex;
  align-items: center;
  justify-content: center;
  color: #6c757d;
`;

interface BuilderCanvasProps {
  elements: any[];
  setElements: (elements: any[]) => void;
  selectedElement: string | null;
  setSelectedElement: (id: string | null) => void;
}

interface SortableElementProps {
  element: any;
  isSelected: boolean;
  onClick: () => void;
}

const SortableElement: React.FC<SortableElementProps> = ({ element, isSelected, onClick }) => {
  const {
    attributes,
    listeners,
    setNodeRef,
    transform,
    transition,
    isDragging
  } = useSortable({ id: element.id });

  const style = {
    transform: CSS.Transform.toString(transform),
    transition,
    opacity: isDragging ? 0.8 : 1,
  };

  const renderElement = () => {
    switch (element.type) {
      case 'text':
        return <div>{element.content}</div>;
      case 'heading':
        return <Heading>{element.content}</Heading>;
      case 'image':
        return <img src={element.src} alt={element.alt} style={{ maxWidth: '100%' }} />;
      case 'button':
        return (
          <button
            style={{
              backgroundColor: element.backgroundColor,
              color: element.color,
              padding: '8px 16px',
              border: 'none',
              borderRadius: '4px',
              cursor: 'pointer',
            }}
          >
            {element.text}
          </button>
        );
      case 'divider':
        return <Divider color={element.color} />;
      case 'spacer':
        return <Spacer height={element.height} />;
      case 'video':
        return (
          <VideoContainer>
            <iframe
              src={element.url}
              title="Video"
              allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture"
              allowFullScreen
            />
          </VideoContainer>
        );
      case 'form':
        return (
          <Form>
            {element.fields.map((field: any, index: number) => (
              <FormField key={index}>
                <label>{field.label}</label>
                <input type={field.type} />
              </FormField>
            ))}
          </Form>
        );
      case 'social':
        return (
          <SocialLinks>
            {element.links.map((link: any, index: number) => (
              <a key={index} href={link.url} target="_blank" rel="noopener noreferrer">
                {link.platform}
              </a>
            ))}
          </SocialLinks>
        );
      case 'map':
        return (
          <MapContainer>
            Map: {element.location} (Zoom: {element.zoom})
          </MapContainer>
        );
      default:
        return null;
    }
  };

  return (
    <ElementWrapper
      ref={setNodeRef}
      {...attributes}
      {...listeners}
      isSelected={isSelected}
      onClick={onClick}
      style={style}
    >
      {renderElement()}
    </ElementWrapper>
  );
};

const BuilderCanvas: React.FC<BuilderCanvasProps> = ({
  elements,
  setElements,
  selectedElement,
  setSelectedElement,
}) => {
  const { setNodeRef, isOver } = useDroppable({
    id: 'canvas',
  });

  const handleElementClick = (id: string) => {
    setSelectedElement(id === selectedElement ? null : id);
  };

  return (
    <CanvasContainer>
      <Canvas ref={setNodeRef} isOver={isOver}>
        {elements.map((element) => (
          <SortableElement
            key={element.id}
            element={element}
            isSelected={element.id === selectedElement}
            onClick={() => handleElementClick(element.id)}
          />
        ))}
      </Canvas>
    </CanvasContainer>
  );
};

export default BuilderCanvas;
