import React from 'react';
import { Droppable, Draggable } from 'react-beautiful-dnd';
import styled from 'styled-components';

const CanvasContainer = styled.div`
  flex: 1;
  padding: 20px;
  background-color: #f8f9fa;
  min-height: 100vh;
`;

const Canvas = styled.div`
  background-color: white;
  border: 1px solid #dee2e6;
  border-radius: 4px;
  min-height: 100%;
  padding: 20px;
`;

const ElementWrapper = styled.div<{ isSelected: boolean }>`
  padding: 10px;
  margin: 5px 0;
  border: 2px solid ${props => props.isSelected ? '#2196f3' : 'transparent'};
  border-radius: 4px;
  cursor: pointer;
  transition: all 0.2s;

  &:hover {
    background-color: #f8f9fa;
  }
`;

interface BuilderCanvasProps {
  elements: any[];
  setElements: (elements: any[]) => void;
  selectedElement: string | null;
  setSelectedElement: (id: string | null) => void;
}

const BuilderCanvas: React.FC<BuilderCanvasProps> = ({
  elements,
  setElements,
  selectedElement,
  setSelectedElement,
}) => {
  const handleElementClick = (id: string) => {
    setSelectedElement(id === selectedElement ? null : id);
  };

  return (
    <CanvasContainer>
      <Droppable droppableId="canvas">
        {(provided) => (
          <Canvas ref={provided.innerRef} {...provided.droppableProps}>
            {elements.map((element, index) => (
              <Draggable key={element.id} draggableId={element.id} index={index}>
                {(provided) => (
                  <ElementWrapper
                    ref={provided.innerRef}
                    {...provided.draggableProps}
                    {...provided.dragHandleProps}
                    isSelected={element.id === selectedElement}
                    onClick={() => handleElementClick(element.id)}
                  >
                    {element.type === 'text' && (
                      <div>{element.content}</div>
                    )}
                    {element.type === 'image' && (
                      <img src={element.src} alt={element.alt} style={{ maxWidth: '100%' }} />
                    )}
                    {element.type === 'button' && (
                      <button
                        style={{
                          backgroundColor: element.backgroundColor,
                          color: element.color,
                          padding: '8px 16px',
                          border: 'none',
                          borderRadius: '4px',
                          cursor: 'pointer',
                        }}
                      >
                        {element.text}
                      </button>
                    )}
                  </ElementWrapper>
                )}
              </Draggable>
            ))}
            {provided.placeholder}
          </Canvas>
        )}
      </Droppable>
    </CanvasContainer>
  );
};

export default BuilderCanvas;
