import React, { useState, useCallback } from 'react';
import styled from 'styled-components';
import { DndContext, DragEndEvent, DragOverlay, useSensor, useSensors, PointerSensor } from '@dnd-kit/core';
import { arrayMove } from '@dnd-kit/sortable';
import ElementPanel from './components/ElementPanel';
import BuilderCanvas from './components/BuilderCanvas';
import PropertyPanel from './components/PropertyPanel';

const AppContainer = styled.div`
  display: flex;
  height: 100vh;
  overflow: hidden;
`;

interface Element {
  id: string;
  type: string;
  content?: string;
  src?: string;
  alt?: string;
  text?: string;
  backgroundColor?: string;
  color?: string;
  height?: string;
  url?: string;
  fields?: Array<{ type: string; label: string }>;
  links?: Array<{ platform: string; url: string }>;
  location?: string;
  zoom?: number;
}

const getDefaultProperties = (type: string): Partial<Element> => {
  switch (type) {
    case 'text':
      return { content: 'New text block' };
    case 'heading':
      return { content: 'New heading' };
    case 'image':
      return { src: 'https://via.placeholder.com/300x200', alt: 'Placeholder image' };
    case 'button':
      return { text: 'Click me', backgroundColor: '#007bff', color: 'white' };
    case 'divider':
      return { color: '#dee2e6' };
    case 'spacer':
      return { height: '20px' };
    case 'video':
      return { url: 'https://www.youtube.com/embed/dQw4w9WgXcQ' };
    case 'form':
      return {
        fields: [
          { type: 'text', label: 'Name' },
          { type: 'email', label: 'Email' },
          { type: 'textarea', label: 'Message' }
        ]
      };
    case 'social':
      return {
        links: [
          { platform: 'Facebook', url: '#' },
          { platform: 'Twitter', url: '#' },
          { platform: 'Instagram', url: '#' }
        ]
      };
    case 'map':
      return { location: 'New York, NY', zoom: 12 };
    default:
      return {};
  }
};

const App: React.FC = () => {
  const [elements, setElements] = useState<Element[]>([]);
  const [selectedElement, setSelectedElement] = useState<string | null>(null);
  const [activeId, setActiveId] = useState<string | null>(null);

  const sensors = useSensors(
    useSensor(PointerSensor, {
      activationConstraint: {
        distance: 8,
      },
    })
  );

  const handleDragEnd = useCallback((event: DragEndEvent) => {
    const { active, over } = event;

    if (!over) {
      setActiveId(null);
      return;
    }

    // If dropping from panel to canvas
    if (typeof active.id === 'string' && active.id.startsWith('panel-')) {
      const elementType = active.id.replace('panel-', '');
      const newElement: Element = {
        id: `${elementType}-${Date.now()}`,
        type: elementType,
        ...getDefaultProperties(elementType)
      };

      setElements(prev => [...prev, newElement]);
      setSelectedElement(newElement.id);
    } 
    // If reordering within canvas
    else if (active.id !== over.id) {
      setElements(prev => {
        const oldIndex = prev.findIndex(e => e.id === active.id);
        const newIndex = prev.findIndex(e => e.id === over.id);
        
        if (oldIndex === -1 || newIndex === -1) return prev;
        
        return arrayMove(prev, oldIndex, newIndex);
      });
    }

    setActiveId(null);
  }, []);

  const handleDragStart = useCallback((event: DragEndEvent) => {
    setActiveId(event.active.id as string);
  }, []);

  const handleElementUpdate = useCallback((id: string, properties: Partial<Element>) => {
    setElements(prev => {
      if (!Array.isArray(prev)) return prev;
      return prev.map(element => 
        element?.id === id ? { ...element, ...properties } : element
      );
    });
  }, []);

  return (
    <DndContext
      sensors={sensors}
      onDragStart={handleDragStart}
      onDragEnd={handleDragEnd}
    >
      <AppContainer>
        <ElementPanel />
        <BuilderCanvas
          elements={elements}
          setElements={setElements}
          selectedElement={selectedElement}
          setSelectedElement={setSelectedElement}
        />
        {selectedElement && (
          <PropertyPanel
            element={elements.find(e => e?.id === selectedElement)}
            onUpdate={(properties) => handleElementUpdate(selectedElement, properties)}
          />
        )}
      </AppContainer>
    </DndContext>
  );
};

export default App;
